INSERT INTO games (id, name, description, min_player, max_player, image_url, date_created, last_updated) VALUES ('45e71ff4-82a9-53f9-adf0-67d612b2a13e', $$HonfoglalÃ³ CsalÃ¡di TÃ¡rsasjÃ¡tÃ©k$$, $$The family edition of the game Honfoglal&oacute;. Players can conquer areas (counties of Hungary) by answering trivia questions. (In some cases it is enough to guess better than your opponent.) But also you have to choose a good strategy in order to conquer the whole country. This edition contains 1200 questions for children and 1200 questions for grown-ups.&#10;&#10;$$, 2, 10, 'https://cf.geekdo-images.com/kVCEVHJDbgX38Kz1T3esKg__original/img/oru6q92XwhhQgjN7kwM4bkgVJyk=/0x0/filters:format(jpeg)/pic6561929.jpg', '2023-11-28 14:31:50', '2023-11-28 14:31:50');
